import React, { useEffect, useState } from 'react';

export default function Profile() {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Получаем токен из localStorage или другого хранилища
    const token = localStorage.getItem('jwtToken'); // замените, если другой способ хранения

    fetch('http://localhost:8080/api/profile', {
      headers: {
        'Authorization': 'Bearer ' + token,
      },
    })
      .then((res) => {
        if (!res.ok) {
          throw new Error('Ошибка загрузки профиля');
        }
        return res.json();
      })
      .then((data) => {
        setUser(data);
        setLoading(false);
      })
      .catch(() => {
        setUser(null);
        setError('Ошибка загрузки данных профиля.');
        setLoading(false);
      });
  }, []);

  if (loading) {
    return <div>Загрузка профиля…</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  // Проверяем поля на наличие, иначе выводим "не указано"
  const renderField = (label, value) =>
    <div>
      <strong>{label}:</strong> {value ? value : 'не указано'}
    </div>;

  return (
    <div className="profile-card">
      <h2>Профиль пользователя</h2>
      {renderField('Email', user.email)}
      {renderField('Имя', user.name)}
      {renderField('Фамилия', user.surname)}
      {renderField('Телефон', user.phone)}
      {/* Добавьте остальные поля по необходимости */}
    </div>
  );
}
