import React, { useState } from 'react';
import Calendar from './Calendar';
import TableMap from './TableMap';

export default function BookingPage() {
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [selectedSeat, setSelectedSeat] = useState(null);

  function handleSelectSeat(tableIdx, seatIdx) {
    setSelectedSeat({ table: tableIdx, seat: seatIdx });
  }

  return (
    <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', marginTop: 40 }}>
      <div style={{ marginBottom: 30 }}>
        <Calendar selectedDate={selectedDate} onSelectDate={setSelectedDate} />
      </div>
      <TableMap selectedSeat={selectedSeat} onSelectSeat={handleSelectSeat} />
      {selectedSeat && (
        <div style={{ marginTop: 24, fontSize: 18, color: '#347cff', fontWeight: 'bold' }}>
          Выбрано: стол №{selectedSeat.table + 1}, место №{selectedSeat.seat + 1}
        </div>
      )}
    </div>
  );
}
