import React, { useState, useEffect } from 'react';

export default function Profile() {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem('jwtToken');
    if (!token) {
      setError('Пользователь не авторизован');
      setLoading(false);
      return;
    }

    fetch('http://localhost:8080/api/profile', {
      method: 'GET',
      headers: {
        'Authorization': 'Bearer ' + token,
        'Content-Type': 'application/json'
      },
    })
      .then(res => {
        if (res.status === 401) {
          throw new Error('Требуется авторизация. Пожалуйста, войдите в систему');
        }
        if (!res.ok) {
          throw new Error('Ошибка загрузки профиля');
        }
        return res.json();
      })
      .then(data => {
        setUser(data);
        setLoading(false);
      })
      .catch(err => {
        setUser(null);
        setError(err.message || 'Ошибка загрузки данных профиля.');
        setLoading(false);
      });
  }, []);

  if (loading) {
    return <div>Загрузка профиля…</div>;
  }

  if (error) {
    return <div style={{ color: 'red' }}>{error}</div>;
  }

  const renderField = (label, value) => (
    <div>
      <strong>{label}:</strong> {value || 'не указано'}
    </div>
  );

  return (
    <div className="profile-card">
      <h2>Профиль пользователя</h2>
      {renderField('Email', user.email)}
      {renderField('Имя', user.name)}
      {renderField('Фамилия', user.surname)}
      {renderField('Телефон', user.phone)}
      {/* Добавьте остальные поля, если нужно */}
    </div>
  );
}
