import React, { useState, useEffect } from 'react';
import Calendar from './Calendar';
import SeatMap from './SeatMap';

export default function BookingPage() {
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [bookedSeats, setBookedSeats] = useState([]);

  useEffect(() => {
    async function fetchBookedSeats() {
      const dateStr = selectedDate.toISOString().slice(0, 10); // yyyy-mm-dd
      try {
        const res = await fetch(`http://localhost:8080/api/bookings/seats?date=${dateStr}`);
        if (!res.ok) throw new Error('Failed to fetch seats');
        const data = await res.json();
        setBookedSeats(data); // ожидаем массив с номерами/ID мест
      } catch (err) {
        console.error(err);
      }
    }
    fetchBookedSeats();
  }, [selectedDate]);

  return (
    <div>
      <h2>Бронирование места</h2>
      <Calendar selectedDate={selectedDate} onSelectDate={setSelectedDate} />
      <SeatMap bookedSeats={bookedSeats} />
    </div>
  );
}
