import React, { useState } from "react";
import ReactCalendar from "react-calendar";
import "react-calendar/dist/Calendar.css";

// Описание столов
const tables = [
  { id: 1, label: "Стол 1", seats: 6 },
  { id: 2, label: "Стол 2", seats: 6 },
  { id: 3, label: "Стол с печеньками", seats: 0, isCookies: true },
  { id: 4, label: "Стол 4", seats: 6 },
  { id: 5, label: "Стол 5", seats: 6 },
  { id: 6, label: "Стол 6", seats: 6 },
];

// Имитация занятых мест
const initialBooked = {};

function getDateKey(date) {
  return date.toISOString().split("T");
}

export default function BookingPage() {
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [selectedTable, setSelectedTable] = useState(null);
  const [selectedSeat, setSelectedSeat] = useState(null);
  const [success, setSuccess] = useState("");
  const [error, setError] = useState("");
  const [booked, setBooked] = useState(initialBooked);

  const currentBookedSeats = () => {
    const dateKey = getDateKey(selectedDate);
    return booked[dateKey]?.[selectedTable] || [];
  };

  const handleBook = () => {
    if (!selectedTable || !selectedSeat) {
      setError("Выберите стол и место!");
      setSuccess("");
      return;
    }
    const dateKey = getDateKey(selectedDate);
    const tableBooked = booked[dateKey]?.[selectedTable] || [];
    if (tableBooked.includes(selectedSeat)) {
      setError("Место уже занято!");
      setSuccess("");
      return;
    }
    setBooked(prevBooked => {
      const newBooked = { ...prevBooked };
      if (!newBooked[dateKey]) newBooked[dateKey] = {};
      if (!newBooked[dateKey][selectedTable]) newBooked[dateKey][selectedTable] = [];
      newBooked[dateKey][selectedTable] = [...newBooked[dateKey][selectedTable], selectedSeat];
      return newBooked;
    });
    setSuccess(
      `Бронирование успешно: ${tables.find(t => t.id === selectedTable).label}, место ${selectedSeat}, дата ${selectedDate.toLocaleDateString()}`
    );
    setError("");
    setSelectedSeat(null);
  };

  const handleTableSelect = (tableId, isCookies) => {
    if (isCookies) return;
    setSelectedTable(tableId);
    if (selectedTable !== tableId) setSelectedSeat(null);
    setError("");
    setSuccess("");
  };

  const handleSeatSelect = (seatNum) => {
    if (currentBookedSeats().includes(seatNum)) {
      setError("Это место уже занято!");
      setSuccess("");
      return;
    }
    setSelectedSeat(seatNum);
    setError("");
    setSuccess("");
  };

  return (
    <div
      style={{
        minHeight: "100vh",
        background: "linear-gradient(120deg, #181e2c 0%, #27213b 100%)",
        color: "#ecebf2",
        padding: "2.5rem 0",
      }}
    >
      <div
        style={{
          maxWidth: 820,
          margin: "0 auto",
          padding: 24,
          background: "rgba(32,35,54,0.93)",
          borderRadius: 22,
          boxShadow: "0 16px 48px #1c1849a0",
        }}
      >
        <h2
          style={{
            textAlign: "center",
            marginBottom: 28,
            fontSize: 30,
            fontWeight: 700,
            background: "linear-gradient(92deg, #19dfa5 0%, #40c6f6 75%)",
            WebkitBackgroundClip: "text",
            WebkitTextFillColor: "transparent",
          }}
        >
          Бронирование места
        </h2>
        <div
          style={{
            display: "flex",
            gap: 44,
            flexWrap: "wrap",
            justifyContent: "center",
            alignItems: "flex-start",
          }}
        >
          <div style={{ minWidth: 300, flex: 1 }}>
            <label
              style={{
                fontWeight: 600,
                marginBottom: 8,
                display: "block",
                color: "#5ec2ff",
              }}
            >
              Выберите дату:
            </label>
            <div
              style={{
                background: "#232c4b",
                borderRadius: 14,
                padding: "10px 12px 16px 12px",
                marginBottom: 22,
              }}
            >
              <ReactCalendar
                onChange={date => {
                  setSelectedDate(date);
                  setSelectedSeat(null);
                  setSuccess("");
                  setError("");
                }}
                value={selectedDate}
                minDate={new Date()}
                calendarType="ISO 8601"
                locale="ru-RU"
                prev2Label={null}
                next2Label={null}
              />
            </div>
          </div>
          <div style={{ minWidth: 370, flex: 2 }}>
            <label
              style={{
                fontWeight: 600,
                marginBottom: 8,
                display: "block",
                color: "#5ec2ff",
              }}
            >
              Выберите стол и место:
            </label>
            <div
              style={{
                display: "flex",
                flexDirection: "column",
                gap: 18,
              }}
            >
              {/* Верхний ряд */}
              <div style={{ display: "flex", gap: 18 }}>
                {tables.slice(0, 3).map(table => (
                  <div key={table.id} style={{ minWidth: 90, textAlign: "center" }}>
                    <div
                      onClick={() => handleTableSelect(table.id, table.isCookies)}
                      style={{
                        padding: "8px 0",
                        borderRadius: 9,
                        background: table.isCookies
                          ? "#fceabb"
                          : selectedTable === table.id
                          ? "linear-gradient(90deg,#19dfa5 0%,#37f4a1 100%)"
                          : "#232c4b",
                        color: table.isCookies ? "#d2691e" : "#fff",
                        fontWeight: 600,
                        boxShadow: "0 2px 8px #232c4bb1",
                        cursor: table.isCookies ? "default" : "pointer",
                        marginBottom: 6,
                        border: table.isCookies ? "2px dashed #FFA07A" : "none",
                        fontSize: 16,
                        userSelect: "none",
                      }}
                    >
                      {table.label}
                      {table.isCookies && " 🍪"}
                    </div>
                    {!table.isCookies && (
                      <div style={{ display: "flex", flexWrap: "wrap", gap: 6, justifyContent: "center" }}>
                        {[...Array(table.seats)].map((_, idx) => {
                          const seatNum = idx + 1;
                          const busy = booked[getDateKey(selectedDate)]?.[table.id]?.includes(seatNum);
                          const selected = selectedSeat === seatNum && selectedTable === table.id;
                          return (
                            <button
                              key={idx}
                              onClick={() => handleSeatSelect(seatNum)}
                              disabled={selectedTable !== table.id || busy}
                              style={{
                                width: 28,
                                height: 28,
                                borderRadius: "50%",
                                border: busy
                                  ? "2px solid #ff4b6c"
                                  : selected
                                  ? "2px solid #19dfa5"
                                  : "1px solid #5ec2ff",
                                background: busy
                                  ? "#ffdddd"
                                  : selected
                                  ? "linear-gradient(90deg,#37f4a1 0%,#40c6f6 100%)"
                                  : "#212433",
                                color: busy ? "#ff4b6c" : selected ? "#222" : "#ecebf2",
                                fontWeight: 700,
                                cursor: busy ? "not-allowed" : selectedTable === table.id ? "pointer" : "not-allowed",
                                opacity: busy ? 0.5 : 1,
                                outline: "none",
                              }}
                            >
                              {seatNum}
                            </button>
                          );
                        })}
                      </div>
                    )}
                  </div>
                ))}
              </div>
              {/* Нижний ряд */}
              <div style={{ display: "flex", gap: 18 }}>
                {tables.slice(3).map(table => (
                  <div key={table.id} style={{ minWidth: 90, textAlign: "center" }}>
                    <div
                      onClick={() => handleTableSelect(table.id, table.isCookies)}
                      style={{
                        padding: "8px 0",
                        borderRadius: 9,
                        background: selectedTable === table.id
                          ? "linear-gradient(90deg,#19dfa5 0%,#37f4a1 100%)"
                          : "#232c4b",
                        color: "#fff",
                        fontWeight: 600,
                        boxShadow: "0 2px 8px #232c4bb1",
                        cursor: "pointer",
                        marginBottom: 6,
                        fontSize: 16,
                      }}
                    >
                      {table.label}
                    </div>
                    <div style={{ display: "flex", flexWrap: "wrap", gap: 6, justifyContent: "center" }}>
                      {[...Array(table.seats)].map((_, idx) => {
                        const seatNum = idx + 1;
                        const busy = booked[getDateKey(selectedDate)]?.[table.id]?.includes(seatNum);
                        const selected = selectedSeat === seatNum && selectedTable === table.id;
                        return (
                          <button
                            key={idx}
                            onClick={() => handleSeatSelect(seatNum)}
                            disabled={selectedTable !== table.id || busy}
                            style={{
                              width: 28,
                              height: 28,
                              borderRadius: "50%",
                              border: busy
                                ? "2px solid #ff4b6c"
                                : selected
                                ? "2px solid #19dfa5"
                                : "1px solid #5ec2ff",
                              background: busy
                                ? "#ffdddd"
                                : selected
                                ? "linear-gradient(90deg,#37f4a1 0%,#40c6f6 100%)"
                                : "#212433",
                              color: busy ? "#ff4b6c" : selected ? "#222" : "#ecebf2",
                              fontWeight: 700,
                              cursor: busy ? "not-allowed" : selectedTable === table.id ? "pointer" : "not-allowed",
                              opacity: busy ? 0.5 : 1,
                              outline: "none",
                            }}
                          >
                            {seatNum}
                          </button>
                        );
                      })}
                    </div>
                  </div>
                ))}
              </div>
            </div>
          </div>
        </div>
        <div style={{
          maxWidth: 410,
          margin: "32px auto 0 auto",
          textAlign: "center",
          padding: "14px 0 0 0"
        }}>
          {error &&
            <div
              className="error"
              style={{
                color: "#ff4b6c",
                fontWeight: 600,
                fontSize: 18,
                marginBottom: 10,
              }}
            >
              {error}
            </div>}
          {success &&
            <div
              className="success"
              style={{
                color: "#19d47a",
                fontWeight: 600,
                fontSize: 18,
                marginBottom: 10,
              }}
            >
              {success}
            </div>}
          <button
            className="misis-btn"
            style={{
              fontSize: 18,
              width: "100%",
              marginTop: 8,
              padding: "0.9rem 0",
              background: "linear-gradient(90deg,#19dfa5 0%,#40c6f6 100%)",
              color: "#222",
              fontWeight: 700,
              borderRadius: 11,
              border: "none",
              boxShadow: "0 2px 8px #2222",
              cursor: "pointer",
              transition: "background 0.18s, color 0.17s, transform 0.13s",
            }}
            onClick={handleBook}
          >
            Подтвердить бронирование
          </button>
        </div>
      </div>
    </div>
  );
}
